changeSet.sql:

		 CREATE TABLE ge_config( `uuid` VARCHAR(32) NOT NULL , `creationTime` DATETIME ,`programName` VARCHAR(32) , 
		`url` VARCHAR(256) , `nameSpace` VARCHAR(256) , `method` VARCHAR(256) , `shortInterval` INT(5) , 
		`longInterval` INT(5) ,  `configName` VARCHAR(64) ,  PRIMARY KEY (`uuid`)  );
		
		ALTER TABLE ge_config ADD UNIQUE `UniqueName` (`configName`);
